graphApp.controller("SideBarCtrl",["$scope","GraphData",function($scope,GraphData){$scope.maxDesc=600,$scope.animate=!1,GraphData.on("select.node",function(event,id,node,links){if($scope.animate=!1,$scope.title=node.title,$scope.related=links,$scope.heroImage=node.image,$scope.imageSize=node.image_size,$scope.description=node.description,node.image){var img=new Image;img.onload=function(){$scope.animate=!0,$scope.$apply()},img.src=node.image}$scope.source=node.source,$scope.sourceURI=node.url,$scope.$apply()}),$scope.loadRelated=function(id){GraphData.emit("select.node",id)},$scope.relatedTitle=function(id){return GraphData.node(id).title}}]);